INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avaler des couleuvres', 'Devoir accepter des choses humiliantes ou des mensonges sans protester.', 'To have to swallow insults or lies without complaining.', '忍气吞声，被迫接受屈辱或谎言。', 'Après cette réunion désastreuse, il a dû avaler des couleuvres.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Chercher midi à quatorze heures', 'Compliquer inutilement une situation simple.', 'To make a mountain out of a molehill; to complicate things unnecessarily.', '把简单的事情复杂化；庸人自扰。', 'Fais simple ! N''allons pas chercher midi à quatorze heures.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Il y a anguille sous roche', 'Il y a quelque chose de caché, une affaire suspecte.', 'There is something fishy going on.', '其中有诈；有些蹊跷。', 'Il est trop gentil aujourd''hui, je suis sûr qu''il y a anguille sous roche.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre du beurre dans les épinards', 'Améliorer ses conditions de vie, gagner un peu plus d''argent.', 'To earn a little extra money; to improve one''s financial situation.', '改善生活；增加一点收入。', 'Ce petit boulot va mettre du beurre dans les épinards.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir d''autres chats à fouetter', 'Avoir d''autres préoccupations plus importantes.', 'To have other fish to fry.', '还有别的重要事情要做。', 'Je ne peux pas m''occuper de ça, j''ai d''autres chats à fouetter.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Filer à l''anglaise', 'Partir discrètement sans dire au revoir.', 'To take French leave.', '不辞而别；偷偷溜走。', 'La fête était ennuyeuse, alors nous avons filé à l''anglaise.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir le cœur sur la main', 'Être très généreux.', 'To contain a heart of gold; to be very generous.', '非常慷慨大方；心地善良。', 'Elle aiderait n''importe qui, elle a vraiment le cœur sur la main.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se mettre le doigt dans l''œil', 'Se tromper grossièrement.', 'To be entirely mistaken.', '大错特错。', 'Tu te mets le doigt dans l''œil si tu crois ça.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Couper les cheveux en quatre', 'S''attarder sur des détails insignifiants.', 'To split hairs.', '钻牛角尖；过分计较细节。', 'Arrête de couper les cheveux en quatre et décide !', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Casser du sucre sur le dos de quelqu''un', 'Dire du mal de quelqu''un en son absence.', 'To talk behind someone''s back.', '在背后说某人坏话。', 'Je déteste l''entendre casser du sucre sur le dos des autres.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être dans de beaux draps', 'Être dans une situation compliquée.', 'To be in a fine mess/pickle.', '处境尴尬；陷入困境。', 'J''ai oublié le dossier, je suis dans de beaux draps.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Faire la pluie et le beau temps', 'Avoir tous les pouvoirs.', 'To call the shots; to rule the roost.', '呼风唤雨；掌握大权。', 'C''est lui qui fait la pluie et le beau temps ici.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir une faim de loup', 'Avoir très faim.', 'To be starving.', '饿得像狼一样；饥肠辘辘。', 'On mange quand ? J''ai une faim de loup !', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre la charrue avant les bœufs', 'Faire les choses dans le mauvais ordre.', 'To put the cart before the horse.', '本末倒置；操之过急。', 'Ne mettons pas la charrue avant les bœufs, attendons la réponse.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tomber dans les pommes', 'S''évanouir.', 'To faint.', '昏倒；晕厥。', 'Il a vu du sang et il est tombé dans les pommes.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir la tête dans les nuages', 'Être distrait, rêveur.', 'To have one''s head in the clouds.', '心不在焉；想入非非。', 'Il oublie tout, il a toujours la tête dans les nuages.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Poser un lapin', 'Ne pas se présenter à un rendez-vous.', 'To stand someone up.', '放鸽子；爽约。', 'Il m''a posé un lapin hier soir.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir un poil dans la main', 'Être très paresseux.', 'To be bone idle.', '极其懒惰。', 'Il ne range jamais rien, il a un poil dans la main.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Monter sur ses grands chevaux', 'Se mettre en colère rapidement.', 'To get on one''s high horse.', '勃然大怒；摆架子。', 'Ne monte pas sur tes grands chevaux, c''était une blague.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Ne pas être sorti de l''auberge', 'Ne pas en avoir fini avec les difficultés.', 'To not be out of the woods yet.', '尚未脱离险境；麻烦还在后头。', 'Avec ce nouveau problème, on n''est pas sorti de l''auberge.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir le cafard', 'Être déprimé, mélancolique.', 'To feel blue; to be down in the dumps.', '情绪低落；郁闷。', 'Il pleut depuis trois jours, j''ai le cafard.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre les pieds dans le plat', 'Aborder un sujet délicat sans maladresse.', 'To put one''s foot in one''s mouth.', '失言；说漏嘴。', 'J''ai mis les pieds dans le plat en parlant de son ex.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se noyer dans un verre d''eau', 'Être dépassé par une difficulté mineure.', 'To make a mountain out of a molehill (panic easily).', '遇小事惊慌失措。', 'Détends-toi, tu te noies dans un verre d''eau.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir la chair de poule', 'Avoir peur ou froid.', 'To have goosebumps.', '起鸡皮疙瘩。', 'Ce film m''a donné la chair de poule.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('C''est la goutte d''eau qui fait déborder le vase', 'L''évènement de trop qui provoque une réaction.', 'The straw that broke the camel''s back.', '压死骆驼的最后一根稻草。', 'Après son retard, cette remarque, c''est la goutte d''eau.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Prendre le taureau par les cornes', 'Affronter une difficulté avec détermination.', 'To take the bull by the horns.', '当机立断；迎难而上。', 'Il faut arrêter d''hésiter et prendre le taureau par les cornes.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être lessivé', 'Être épuisé, très fatigué.', 'To be washed out/exhausted.', '筋疲力尽。', 'Je vais me coucher, je suis lessivé.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir un chat dans la gorge', 'Être enroué.', 'To have a frog in one''s throat.', '嗓子哑了。', 'Désolé, je ne peux pas chanter, j''ai un chat dans la gorge.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Raconter des salades', 'Dire des mensonges.', 'To tell tall tales/lies.', '胡说八道；编故事。', 'Arrête de me raconter des salades !', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Coup de foudre', 'Tomber amoureux immédiatement.', 'Love at first sight.', '一见钟情。', 'Quand ils se sont vus, ça a été le coup de foudre.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir la main verte', 'Être doué pour le jardinage.', 'To have a green thumb.', '擅长园艺。', 'Son jardin est magnifique, elle a la main verte.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Passer une nuit blanche', 'Ne pas dormir de la nuit.', 'To have a sleepless night.', '彻夜未眠。', 'J''ai passé une nuit blanche à réviser.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se serrer la ceinture', 'Économiser, réduire ses dépenses.', 'To tighten one''s belt.', '勒紧裤腰带；省吃俭用。', 'Ce mois-ci, il va falloir se serrer la ceinture.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir le bras long', 'Avoir des relations influentes.', 'To have friends in high places.', '神通广大；人脉广。', 'Il pourra t''aider, il a le bras long.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Broyer du noir', 'Être déprimé, voir tout en négatif.', 'To be down in the dumps.', '闷闷不乐；悲观。', 'Il ne fait que broyer du noir depuis son échec.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Faire la tournée des grands ducs', 'Sortir, faire la fête luxueusement.', 'To paint the town red.', '花天酒地；狂欢。', 'Pour son anniversaire, on a fait la tournée des grands ducs.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir du pain sur la planche', 'Avoir beaucoup de travail à faire.', 'To have a lot on one''s plate.', '任务繁重；有很多工作要做。', 'Je ne peux pas sortir, j''ai du pain sur la planche.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre la main à la pâte', 'Aider, participer au travail.', 'To pitch in.', '亲自动手；帮忙。', 'Tout le monde doit mettre la main à la pâte pour finir à temps.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('C''est simple comme bonjour', 'C''est très facile.', 'It''s easy as pie.', '易如反掌。', 'Réparer ce truc, c''est simple comme bonjour.', 'A2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tomber dans le panneau', 'Se faire piéger, croire un mensonge.', 'To fall into the trap.', '上当受骗。', 'C''était une arnaque et je suis tombé dans le panneau.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir les yeux plus gros que le ventre', 'Prendre plus de nourriture qu''on ne peut en manger.', 'To have eyes bigger than one''s stomach.', '眼阔肚窄；贪多嚼不烂。', 'Je n''ai pas pu finir mon assiette, j''ai eu les yeux plus gros que le ventre.', 'B1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Ne pas avoir sa langue dans sa poche', 'Parler facilement, ne pas hésiter à répondre.', 'To not be tongue-tied; to have plenty to say.', '伶牙俐齿；直言不讳。', 'Elle vous répondra, elle n''a pas sa langue dans sa poche.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Faire l''autruche', 'Refuser de voir le danger ou la réalité.', 'To bury one''s head in the sand.', '自欺欺人；逃避现实。', 'Ignorer les dettes, c''est faire l''autruche.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Jeter l''argent par les fenêtres', 'Dépenser sans compter, gaspiller.', 'To throw money down the drain.', '挥霍浪费。', 'Il achète des choses inutiles, il jette l''argent par les fenêtres.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être connu comme le loup blanc', 'Être très connu de tout le monde.', 'To be known by everyone.', '众所周知；赫赫有名。', 'Dans ce village, il est connu comme le loup blanc.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Reprendre du poil de la bête', 'Reprendre des forces, aller mieux.', 'To bounce back; to regain health.', '恢复元气；重振旗鼓。', 'Après une semaine de repos, il a repris du poil de la bête.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se faire l''avocat du diable', 'Défendre une opinion contraire à la sienne ou à celle de la majorité pour stimuler le débat.', 'To play devil''s advocate.', '扮演魔鬼代言人；故意唱反调。', 'Je suis d''accord avec toi, mais je vais me faire l''avocat du diable pour tester tes arguments.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tirer les marrons du feu', 'Tirer profit d''une situation risquée grâce au travail des autres.', 'To pull the chestnuts out of the fire; to profit from others'' work.', '坐享其成；火中取栗。', 'Il a laissé ses collègues prendre tous les risques et c''est lui qui a tiré les marrons du feu.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir le vent en poupe', 'Être dans une période de succès, être favorisé par les circonstances.', 'To be riding high; to have the wind in one''s sails.', '顺风顺水；事业蒸蒸日上。', 'L''entreprise a le vent en poupe depuis le lancement de son nouveau produit.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre de l''eau dans son vin', 'Être plus modéré, faire des concessions.', 'To water down one''s wine; to compromise/tone it down.', '做出让步；缓和语气。', 'Il était très en colère, mais il a fini par mettre de l''eau dans son vin.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Renvoyer l''ascenseur', 'Rendre un service à quelqu''un qui nous a aidé.', 'To return the favor.', '投桃报李；回报。', 'Tu m''as beaucoup aidé l''année dernière, c''est normal que je te renvoie l''ascenseur.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Pousser le bouchon un peu trop loin', 'Exagérer, dépasser les limites.', 'To go too far; to cross the line.', '做得太过分；欺人太甚。', 'Là, tu pousses le bouchon un peu trop loin, je ne peux pas accepter ça.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Vendre la peau de l''ours avant de l''avoir tué', 'Se féliciter d''un succès qui n''est pas encore acquis.', 'To count one''s chickens before they hatch.', '高兴得太早；未捉到熊通过先卖皮。', 'Attends les résultats officiels, ne vends pas la peau de l''ours avant de l''avoir tué.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Faire chou blanc', 'Échouer, ne rien obtenir.', 'To draw a blank; to fail.', '白忙一场；一无所获。', 'Les enquêteurs ont fait chou blanc, ils n''ont trouvé aucun indice.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tomber sur un os', 'Rencontrer une difficulté imprévue.', 'To hit a snag.', '遇到棘手问题；碰钉子。', 'Le projet avançait bien mais nous sommes tombés sur un os avec la réglementation.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être sur la sellette', 'Être accusé, suspecté ou en position critique.', 'To be in the hot seat.', '受到严厉盘问；处境危险。', 'Le directeur est sur la sellette après ces mauvais résultats.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre les points sur les i', 'Clarifier une situation, expliquer fermement.', 'To dot the i''s and cross the t''s; to clarify things firmly.', '把话说清楚；澄清事实。', 'Il y a eu trop de malentendus, il est temps de mettre les points sur les i.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Ne pas y aller par quatre chemins', 'Aller droit au but, parler franchement.', 'To get straight to the point; not to beat around the bush.', '直截了当；开门见山。', 'Il n''y est pas allé par quatre chemins pour lui annoncer la nouvelle.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Rire jaune', 'Se forcer à rire pour cacher son dépit ou sa gêne.', 'To give a forced laugh.', '强颜欢笑；苦笑。', 'Quand il a vu les dégâts, il a ri jaune.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir plusieurs cordes à son arc', 'Avoir plusieurs compétences ou moyens d''action.', 'To have more than one string to one''s bow.', '有多手准备；多才多艺。', 'Elle parle trois langues et sait coder, elle a plusieurs cordes à son arc.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Battre le fer tant qu''il est chaud', 'Profiter immédiatement d''une occasion favorable.', 'To strike while the iron is hot.', '趁热打铁。', 'Le client est intéressé, il faut battre le fer tant qu''il est chaud.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre la puce à l''oreille', 'Éveiller les soupçons ou l''attention.', 'To arouse suspicion; to put a bug in one''s ear.', '引起怀疑；使人警觉。', 'Ce qu''il a dit m''a mis la puce à l''oreille.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Décrocher la lune', 'Obtenir l''impossible.', 'To give someone the moon; to do the impossible.', '摘下月亮；做不可能的事。', 'Pour elle, il serait prêt à décrocher la lune.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir du fil à retordre', 'Avoir des difficultés à résoudre un problème ou à gérer quelqu''un.', 'To have a hard time with something/someone.', '遇到棘手的事；不好对付。', 'Ce dossier va nous donner du fil à retordre.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être au four et au moulin', 'Être occupé à plusieurs choses à la fois.', 'To be in two places at once; to burn the candle at both ends.', '身兼数职；分身乏术。', 'On ne peut pas être au four et au moulin, il faut choisir.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Couper l''herbe sous le pied', 'Devancer quelqu''un et le priver de son avantage.', 'To pull the rug out from under someone.', '捷足先登；抢先一步。', 'J''allais proposer cette idée, mais il m''a coupé l''herbe sous le pied.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Faire d''une pierre deux coups', 'Atteindre deux objectifs avec une seule action.', 'To kill two birds with one stone.', '一石二鸟；一举两得。', 'En allant à Paris pour le travail, j''en profiterai pour voir mes amis, je ferai d''une pierre deux coups.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Manger de la vache enragée', 'Vivre des moments difficiles, subir des privations.', 'To have a rough time of it; to go through hard times.', '过苦日子；受穷。', 'Au début de sa carrière, il a mangé de la vache enragée.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Pendre la crémaillère', 'Fêter son installation dans un nouveau logement.', 'To have a housewarming party.', '乔迁请客；温居。', 'Nous allons pendre la crémaillère samedi prochain.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir une dent contre quelqu''un', 'En vouloir à quelqu''un, lui garder rancune.', 'To hold a grudge against someone.', '怀恨在心；对某人有意见。', 'Depuis leur dispute, il a une dent contre elle.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Crier au loup', 'Alerter sans raison, au risque de ne plus être cru.', 'To cry wolf.', '谎报军情；喊“狼来了”。', 'À force de crier au loup, personne ne l''écoute quand c''est grave.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre les bouchées doubles', 'Accélérer le travail, faire des efforts supplémentaires.', 'To double one''s efforts.', '加倍努力；加班加点。', 'Le projet est en retard, il faut mettre les bouchées doubles.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Rouler quelqu''un dans la farine', 'Duper quelqu''un, l''arnaquer.', 'To con someone; to take someone for a ride.', '欺骗某人；把某人骗得团团转。', 'Il s''est fait rouler dans la farine par ce vendeur.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se renvoyer la balle', 'Se rejeter mutuellement la responsabilité.', 'To pass the buck.', '互相推诿；踢皮球。', 'Les deux départements se renvoient la balle au lieu de régler le problème.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tenir la jambe à quelqu''un', 'Ennuyer quelqu''un avec une conversation interminable.', 'To bore someone with endless talk.', '缠着某人唠叨；拉住某人闲聊。', 'Mon voisin m''a tenu la jambe pendant une heure.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Vider son sac', 'Dire tout ce qu''on a sur le cœur, exprimer ses rancunes.', 'To get something off one''s chest.', '一吐为快；倾诉衷肠。', 'Il avait besoin de vider son sac après toutes ces années de silence.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Y mettre du sien', 'Faire un effort, participer de bonne volonté.', 'To do one''s bit/part; to make an effort.', '尽自己的一份力；做出让步。', 'Si tout le monde y met du sien, on finira plus vite.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Avoir le compas dans l''œil', 'Avoir le coup d''œil juste, évaluer les distances avec précision.', 'To have a good eye (for measurements).', '目测准确；眼力好。', 'Il a garé la voiture au millimètre, il a le compas dans l''œil.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Bâtir des châteaux en Espagne', 'Faire des projets irréalisables.', 'To build castles in the air.', '想入非非；空中楼阁。', 'Arrête de bâtir des châteaux en Espagne, sois réaliste.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Ce n''est pas la mer à boire', 'Ce n''est pas si difficile que ça.', 'It''s not asking the moon; it''s not that hard.', '这没什么难的；并非难如登天。', 'Faire cet exercice, ce n''est quand même pas la mer à boire.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être comme un coq en pâte', 'Être très à l''aise, choyé.', 'To live in clover; to be pampered.', '养尊处优；生活舒适。', 'Chez sa grand-mère, il est comme un coq en pâte.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Être le dindon de la farce', 'Être la victime d''une moquerie ou d''une affaire.', 'To be the dupe/laughing stock.', '成为笑柄；受愚弄。', 'J''ai payé pour tout le monde, j''ai été le dindon de la farce.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Filer un mauvais coton', 'Avoir des problèmes de santé ou de comportement qui s''aggravent.', 'To go downhill; to be in a bad way.', '情况不妙；每况愈下。', 'Il ne travaille plus et sort tous les soirs, il file un mauvais coton.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Jeter l''éponge', 'Abandonner une compétition ou une entreprise difficile.', 'To throw in the towel.', '认输；放弃。', 'Après avoir tout essayé, il a fini par jeter l''éponge.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('L''habit ne fait pas le moine', 'Il ne faut pas se fier aux apparences.', 'Don''t judge a book by its cover.', '人不可貌相。', 'Il a l''air sévère mais il est très gentil, l''habit ne fait pas le moine.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mener quelqu''un en bateau', 'Tromper quelqu''un en lui racontant des histoires.', 'To lead someone on; to take someone for a ride.', '愚弄某人；欺骗。', 'Il m''a mené en bateau pendant des mois avec ses fausses promesses.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Mettre la main au feu', 'Être absolument sûr de quelque chose.', 'To bet one''s life on something.', '敢发誓；确信无疑。', 'C''est lui le coupable, j''en mettrais ma main au feu.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Ne pas être dans son assiette', 'Ne pas se sentir bien (physiquement ou moralement).', 'To feel under the weather.', '身体不适；不在状态。', 'Je vais rentrer, je ne suis pas dans mon assiette aujourd''hui.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Passer l''arme à gauche', 'Mourir (familier).', 'To kick the bucket.', '一命呜呼；挂了。', 'Le vieux voisin a passé l''arme à gauche la semaine dernière.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Pleuvoir des cordes', 'Pleuvoir très fort.', 'To rain cats and dogs.', '倾盆大雨。', 'On ne peut pas sortir, il pleut des cordes.', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Prendre des vessies pour des lanternes', 'Se faire des illusions grossières, se laisser tromper.', 'To be easily gulled; to believe the moon is made of green cheese.', '把由头当灯笼；异想天开。', 'Il est naïf, on lui fait prendre des vessies pour des lanternes.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Rire comme une baleine', 'Rire très fort et la bouche grande ouverte.', 'To laugh one''s head off.', '哈哈大笑；笑得合不拢嘴。', 'À sa blague, tout le monde a ri comme une baleine.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se faire porter pâle', 'Prétexter une maladie pour ne pas venir travailler.', 'To call in sick (often falsely).', '装病请假。', 'Il n''avait pas envie de venir, il s''est fait porter pâle.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Se mettre en quatre', 'Faire le maximum pour quelqu''un.', 'To bend over backwards.', '竭尽全力；为某人操劳。', 'Elle s''est mise en quatre pour organiser cette fête.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tirer son épingle du jeu', 'Se dégager habilement d''une situation difficile.', 'To play one''s cards right; to come out smelling of roses.', '全身而退；摆脱困境。', 'Malgré la crise, l''entreprise a su tirer son épingle du jeu.', 'C2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tomber des nues', 'Être extrêmement surpris.', 'To be flabbergasted; to fall from the sky.', '大吃一惊；如梦初醒。', 'Quand j''ai appris la nouvelle, je suis tombé des nues.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Tourner autour du pot', 'Hésiter à dire les choses franchement.', 'To beat around the bush.', '拐弯抹角；旁敲侧击。', 'Dis-moi ce que tu veux, arrête de tourner autour du pot !', 'B2') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Voir midi à sa porte', 'Juger les choses selon son propre point de vue ou ses intérêts.', 'To see things from one''s own perspective.', '从自己的角度看问题。', 'Il ne pense qu''à lui, il voit toujours midi à sa porte.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Vouloir le beurre et l''argent du beurre', 'Tout vouloir sans rien sacrifier.', 'To want to have one''s cake and eat it too.', '鱼与熊掌兼得；贪得无厌。', 'Il veut un produit de qualité mais pas cher, il veut le beurre et l''argent du beurre.', 'C1') ON CONFLICT (expression) DO NOTHING;
INSERT INTO vocabulary_items (expression, meaning, meaning_en, meaning_zh, example, level) VALUES ('Les carottes sont cuites', 'Tout est perdu, il n''y a plus d''espoir.', 'The goose is cooked; the chips are down.', '大势已去；无力回天。', 'On ne peut plus rien faire, les carottes sont cuites.', 'C1') ON CONFLICT (expression) DO NOTHING;
